import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.io.PrintWriter;import java.net.Socket;public class ServerWiFi {    private BufferedReader in_ch    = null;    private PrintWriter out         = null;    private Socket fromServer       = null;    private char [] ch_Ident        = new char[30];    private char [] ch_Data         = new char[30];    private int [] int_Ident        = new int[28];    private int [] int_Data         = new int[340];    public ServerWiFi() {        System.out.println("Welcome to server!");        try{            fromServer = new Socket("192.168.4.1",8108);            System.out.println("Connected...");            in_ch  = new BufferedReader(new InputStreamReader(fromServer.getInputStream()));            out = new PrintWriter(fromServer.getOutputStream());        } catch (Exception e) {            System.out.println("Can't open port!");            System.exit(1);        }    }    public char[]   getCh_Ident() {        return ch_Ident;    }    public char[]   getCh_Data() {        return ch_Data;    }    public int[]    getInt_Ident() {        return int_Ident;    }    public int[]    getInt_Data() {        return int_Data;    }    public void     sendIdentificationRequest() throws IOException {        out.println("Identification.");        out.flush();        in_ch.read(ch_Ident);        System.out.print("Send identification. The response of server: ");        for (int i=0; i<ch_Ident.length; i++){            System.out.print(ch_Ident[i]);            ch_Ident[i] = (char) 0;        }        System.out.println();        in_ch.read(ch_Ident);        System.out.print("The data response of server: ");        for (int i=0; i<ch_Ident.length; i++){            System.out.print(ch_Ident[i]);            ch_Ident[i] = (char) 0;        }        System.out.println();    }    public void     sendDataRequest() throws IOException {        out.println("Give me data.");        out.flush();        in_ch.read(ch_Data);        System.out.print("Send Give me data. The response of server: ");        for (int i=0; i<ch_Data.length; i++){            System.out.print(ch_Data[i]);            ch_Data[i] = (char) 0;        }        System.out.println();        in_ch.read(ch_Data);        System.out.print("Send Give me data. The response of server: ");        for (int i=0; i<ch_Data.length; i++){            System.out.print(ch_Data[i]);            ch_Data[i] = (char) 0;        }        System.out.println();    }}